 body {
      font-family: Arial, sans-serif;
      margin: 20px;
      padding: 0;
      background-color: #f5f5f5;
    }

    .container {
      background-color: #fff;
      padding: 20px;
      /* max-width: 700px; */
      margin: 0 auto;
      box-shadow: 0 0 10px rgba(0,0,0,0.1);
    }

    /* Header / Logo Section */
    .header {
      display: flex;
      align-items: center;
      margin-bottom: 20px;
    }

    .header img {
      height: 50px;
      width: 50px;
      margin-right: 10px;
    }

    .header h1 {
      font-size: 22px;
      margin: 0;
    }

    /* Form Fields */
    .form-section {
      display: flex;
      flex-wrap: wrap;
      margin-bottom: 15px;
    }
    .form-section label {
      width: 120px;
      font-weight: bold;
      margin-right: 10px;
      margin-bottom: 5px;
    }
    .form-section input {
      flex: 1;
      padding: 5px;
      margin-bottom: 5px;
    }

    /* Table Styles */
    table {
      width: 100%;
      border-collapse: collapse;
      margin-bottom: 20px;
      text-align: center;
    }
    table, th, td {
      border: 1px solid #000;
    }
    th, td {
      padding: 8px;
    }
    thead th {
      background-color: #e9e9e9;
    }

    /* Signature Section */
    .signature {
      margin-top: 10px;
    }
    .signature label {
      font-weight: bold;
      margin-right: 10px;
    }
    .signature input {
      padding: 5px;
    }

