css:
@import url("https://fonts.googleapis.com/css2?family=Playfair+Display:ital@1&family=Poppins:wght@300;400;500&display=swap");

* {
  padding: 0;
  margin: 0;
  box-sizing: border-box;
}

body {
  box-sizing: inherit;
}

/* .main{
  background-color: rgb(240, 212, 212);
} */

/* ..................section 1 */
.one {
  display: flex;
  justify-content: center;
  align-items: center;
}
.name {
  font-weight: 200;
}
.name {
  border-left: none;
  border-right: none;
  border-top: none;
  border-bottom: 1px solid black;
  padding: 3px 0px 4px 10px;
}

.h4_name {
  color: black;
  font-weight: bold;
  font-size: 20px;
  margin-top: 20px;
  width: 100%;
}

.birth_p_one {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 30px;
}

.header {
  display: flex;
  flex-direction: column;
  justify-content: center;
  flex-direction: column;
  width: 100%;
  margin-top: 50px;
}
.header:nth-child(1) {
  text-align: center;
}
.header h2 {
  font-size: 16px;
  font-family: "Playfair Display", serif;
  color: rgb(46, 46, 155);
}

.birth_info {
  display: flex;
  margin-left: 30px;
}

.info_left {
  display: flex;
  flex-direction: column;
  gap: 20px;
}

.info_right {
  display: flex;
  gap: 20px;
  margin-left: 30px;
}

.round {
  height: 210px;
  width: 120px;
  border: 1px solid black;
  border-radius: 40%;
  margin-left: 20px;
}

/* .........................section 2 */
.birth_p_two {
  display: flex;
  flex-direction: column;
  gap: 40px;
  justify-content: center;
  align-items: center;
  margin-top: 20px;
}

.birth_p_two_info {
  display: flex;
  flex-direction: column;
  gap: 20px;
  margin-left: 50px;
}

.palce_district {
  display: flex;
  align-items: center;
  gap: 30px;
}

.p_place_dict {
  color: rgb(0, 0, 0);
  font-weight: bold;
  font-size: 1.2rem;
  color: rgb(46, 46, 155);
}
.registration {
  display: flex;
  align-items: center;
  gap: 30px;
}
.one_sec2 {
  display: flex;
  justify-content: center;
  align-items: center;
}

.h4_name_sec2 {
  color: rgb(46, 46, 155);
  font-weight: bold;
  font-size: 20px;
  margin-top: 20px;
  width: 100%;
}
.name_sec2 {
  border-left: none;
  border-right: none;
  border-top: none;
  border-bottom: 1px solid black;
  padding: 13px 0px 4px 10px;
  width: 100%;
}

.a {
  color: rgb(46, 46, 155);
  width: 30%;
  font-weight: bold;
  font-size: 20px;
  margin-top: 20px;
}
.pare {
  display: flex;
  flex-direction: row;
}

.parent {
  border-left: none;
  border-right: none;
  border-top: none;
  border-bottom: 1px solid black;
  padding: 13px 0px 4px 10px;
  width: 50%;
}

.weight_sex {
  display: flex;
  align-items: center;
  gap: 70px;
}

/* ...................footer */
.footer {
  display: flex;
  flex-direction: column;
  margin-left: 90px;
  margin-bottom: 20px;
}

.footer h1 {
  color: rgb(46, 46, 155);
  font-weight: bold;
  font-size: 2rem;
}

.act {
  color: rgb(46, 46, 155);
}

.saxiix {
  display: flex;
}
.saxi_text {
  margin-top: 20px;
  font-weight: bold;
  font-size: 20px;
  color: rgb(46, 46, 155);
}
.saxi_line {
  border-left: none;
  border-right: none;
  border-top: none;
  border-bottom: 1px solid black;
  padding: 13px 0px 4px 10px;
  width: 30%;
  margin-left: 20px;
}

/* ....................... page breaks */
@media print {
  .page_break {
    page-break-before: always;
  }
}
