css:
* {
  padding: 0;
  margin: 0;
  box-sizing: border-box;
}

.main_logo {
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  border-bottom: 1px solid gray;
  margin: 0px 10px 0px 10px;
}
.logo_left img {
  width: 300px;
  height: 100px;
}
.right_logo h4 {
  text-align: center;
}
.right_logo {
  display: flex;
}
.verifed_icon {
  font-size: 40px;
  color: green;
}
.number {
  padding: 14px 0px 0px 30px;
  font-size: 25px;
}
/* .................. mian_info */
h1,
h2,
h3,
h4,
h5,
h6{
    padding:0px;
    margin:0px;
}
.mian_info {
  display: flex;
  width: 100%;
  justify-content: center;
  padding-top: 10px;
}
.info_2 {
  margin-left: 100px;
}
.info_id,
.info_age {
  display: flex;
  padding: 10px 0px 0px 0px;
}

.info_id_2,
.info_age_2,
.info_doctor_2 {
  display: flex;
  padding: 10px 0px 0px 0px;
}
/* ...... */
.info_id h4 {
  color: #9478a9;
  margin-left: 90px;
  border: 1px solid rgb(141, 225, 141);
  border-radius: 50px;
  text-align: left;
  padding: 4px 50px 4px 50px;
}
.info_age h4 {
  color: #9478a9;
  margin-left: 70px;
}
.info_doctor h4 {
  color: #9478a9;
  padding-left: 64px;
}
/* ....... */
.info_id_2 h4 {
  color: #9478a9;
  margin-left: 100px;
  border: 1px solid rgb(141, 225, 141);
  border-radius: 50px;

  padding: 4px 50px 4px 50px;
}
.info_age_2 h4 {
  color: #9478a9;
  padding-left: 127px;
}
.info_doctor_2 h4 {
  color: #9478a9;
  padding-left: 125px;
}

.info_id h3,
.info_age h3 {
  color: #153b50;
}

/* ........... table */
/* ...........table_section */
.table_section {
  display: flex;
  justify-content: center;
  padding-top: 10px;
  margin: 10px;
}
table {
  border-collapse: collapse;
}

table td {
  border-bottom: 1px solid gray;
  border-top: 1px solid gray;
  padding: 20px 0px 20px 0px;
}
table th {
  padding: 10px 30px 10px 30px;
  text-align: left;
  color: gray;
  font-size: 12px;
}
table th:nth-child(2) {
  width: 460px;
}

table td {
  padding: 10px 30px 10px 30px;
  text-align: left;
  /* color: rgb(90, 71, 109); */
  color: #342042;
}

/* ............... sub totals */
.finance_office {
  display: flex;
  flex-direction: column;
  margin-left: 260px;
  padding-top: 20px;
}
.finance_office h3{
    padding-left: 50px;
}
.main_total {
  float: right;
  margin-right: 160px;
}
.total,
.discount,
.grand,
.amount,
.balance {
  display: flex;
  margin-right: 30px;
  padding: 3px 0px 3px 0px;
  color: rgb(88, 88, 88);
}
.total h4 {
  padding-left: 80px;
  text-align: left;
}
.discount h4 {
  padding-left: 80px;
  text-align: left;
}
.grand h4 {
  padding-left: 50px;
  text-align: left;
}
.amount h4 {
  padding-left: 40px;
  text-align: left;
}
.balance h4 {
  padding-left: 40px;
  text-align: center;
  border: 1px solid rgb(141, 225, 141);
  border-radius: 50px;
  padding: 3px 50px 3px 50px;
}
.thanks {
  /*padding-top: 170px;*/
  /*color: rgb(88, 88, 88);*/
  /*display: flex;*/
  /*justify-content:center;*/

}

/* ..................... adress */
.thanks{
    display:flex;
    justify-content:center;
     border-bottom: 2px dotted #1c146b;
}
.main_address{
    position:fixed;
    bottom:0;
  
}
.address {
    display: flex;
    justify-content: center;
    padding-top: 20px;
    color: #342042;
   
  }

  .address h4 {
    font-size: 14px;
  }
  .address h4 span {
    padding: 0px 5px 0px 5px;
  }
  
@media print {
  .main_total {
    float: right;
    margin-right: 80px;
  }
  .finance_office {
    display: flex;
    flex-direction: column;
    margin-left: 100px;
  
    padding-top: 20px;
  }
  .finance_office h3{
        text-align: left;
  }
.info_id_2 h3:nth-child(1){
  width: 100px;
}
.info_id_2 h4 {
  color: #9478a9;
  margin-left: 50px;
  border: 1px solid rgb(141, 225, 141);
  border-radius: 50px;

  padding: 4px 50px 4px 50px;
}

}
